import { useEffect,useState } from "react";
import PortfolioList from "../portfolioList/PortfolioList";
import "./portfolio.scss";
import {
    jeuxPortfolio,
    mobilePortfolio,
    logicielPortfolio,
    algorithmePortfolio,
    securitePortfolio,
  } from "../../data";

  export default function Portfolio() {
    const [selected, setSelected] = useState("featured");
    const [data, setData] = useState([]);
    const list = [ {
        id: "jeux",
        title: "Jeux",
      },
      {
        id: "mobile",
        title: "Mobile App",
      },
      {
        id: "logiciel",
        title: "Logiciel",
      },
      {
        id: "algorithme",
        title: "Algorithme",
      },
      {
        id: "securite",
        title: "Sécurité",
      },
    ];

    useEffect(() => {
      switch (selected) {
        case "jeux":
          setData(jeuxPortfolio);
          break;
        case "mobile":
          setData(mobilePortfolio);
          break;
        case "logiciel":
          setData(logicielPortfolio);
          break;
        case "algorithme":
          setData(algorithmePortfolio);
          break;
          case "securite":
            setData(securitePortfolio);
            break;
        default:
          setData(jeuxPortfolio);
      }
    }, [selected]);

    return (
      <div className="portfolio" id="portfolio">
        <h1>Portfolio</h1>
        <div className="contain">
          <div className="card">
            <div className="imgBx">
              <img src="contact.jpg"></img>
            </div>
            <div className="content">
              <h2>Card 1</h2>
            </div>
          </div>
        </div>
          )
      </div>
    );
  }
