import "./competence.scss";

export default function Competence() {
  const data = [
    {
      id: 1,
      title: "Framework",
      img:
        "https://img2.freepng.fr/20180607/kfb/kisspng-software-developer-computer-programming-cooperpres-keith-white-5b19216d69a3c0.1783053515283736134327.jpg",
      icon: "assets/twitter.png",
      desc:
        "React",
    },
    {
      id: 2,
      title: "Langages informatiques",
      img:
        "https://img2.freepng.fr/20180514/cpq/kisspng-source-code-computer-icons-computer-programming-5af9727ca535a9.9706103115262972126767.jpg",
      icon: "assets/youtube.png",
      desc:
        "HTML,CSS,JavaScript,Java,PHP,Python,C",
      featured: true,
    },
    {
      id: 3,
      title: "Maîtrises informatiques",
      img:
        "https://cdn-icons-png.flaticon.com/512/2748/2748829.png",
      icon: "assets/linkedin.png",
      desc:
        "Git",
        desc2:
        "Linux",
        desc3:
        "Shell/Bash",
        desc4:
        "Node.js",
        desc5:
        "Android Studio",
    },
  ];
  return (
    <div className="competence" id="competence">
      <h1>Compétences</h1>
      <div className="container">
        {data.map((d) => (
          <div className={d.featured ? "card featured" : "card"}>
            <div className="top">
              <img className="left" alt="" />
              <img
                className="user"
                src={d.img}
                alt=""
              />
              <img className="right" src={d.icon} alt="" />
            </div>
            <div className="center">
             <div className="colone"> {d.desc}
              {d.desc2}
              {d.desc3}
              {d.desc4}
              {d.desc5}
              </div>
            </div>
            <div className="bottom">
              <h3>{d.name}</h3>
              <h4>{d.title}</h4>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
