import { useEffect,useState } from "react";
import PortfolioList from "../portfolioList/PortfolioList";
import "./portfolio.scss";
import {
    jeuxPortfolio,
    mobilePortfolio,
    logicielPortfolio,
    algorithmePortfolio,
    securitePortfolio,
  } from "../../data";

  export default function Portfolio() {
    const [selected, setSelected] = useState("featured");
    const [data, setData] = useState([]);
    const list = [ {
        id: "jeux",
        title: "Jeux",
      },
      {
        id: "mobile",
        title: "Mobile App",
      },
      {
        id: "logiciel",
        title: "Logiciel",
      },
      {
        id: "algorithme",
        title: "Algorithme",
      },
      {
        id: "securite",
        title: "Sécurité",
      },
    ];

    useEffect(() => {
      switch (selected) {
        case "jeux":
          setData(jeuxPortfolio);
          break;
        case "mobile":
          setData(mobilePortfolio);
          break;
        case "logiciel":
          setData(logicielPortfolio);
          break;
        case "algorithme":
          setData(algorithmePortfolio);
          break;
          case "securite":
            setData(securitePortfolio);
            break;
        default:
          setData(jeuxPortfolio);
      }
    }, [selected]);

    return (
      <div className="portfolio" id="portfolio">
        <h1>Portfolio</h1>
        <ul>
          {list.map((item) => (
            <PortfolioList
              title={item.title}
              active={selected === item.id}
              setSelected={setSelected}
              id={item.id}
            />
          ))}
        </ul>
        <div className="container" style={{backgroundImage : url(d.img)}}>
          {data.map((d) => (
            <div className="item">
              <h3>{d.title}</h3>
            </div>
          ))}
        </div>
      </div>
    );
  }
